mapper.select:
select b_num, memberid, nickname, b_title, b_wdate, b_hit, cateid from board where cateid = #{cateid}
AND (nickname LIKE CONCAT('%',#{keyword},'%') or b_title like CONCAT('%',#{keyword},'%') or b_content like CONCAT('%',#{keyword},'%'))
order by b_num desc LIMIT #{startList}, #{listSize}
